                          c(i,j) = alpha*temp + beta*c(i,j)
                      endif
                  enddo
              enddo
          else
!
!           Form  C := alpha*A**T*B + beta*C
!
              do j = 1,n
                  do i = 1,m
                      temp = zero
                      do l = 1,k
                          temp = temp + a(l,i)*b(l,j)
                      enddo
                      if (beta.eq.zero) then
                          c(i,j) = alpha*temp
                      else
                          c(i,j) = alpha*temp + beta*c(i,j)
                      endif
                  enddo
              enddo
          endif
      elseif (nota) then
          if (conjb) then
!
!           Form  C := alpha*A*B**H + beta*C.
!
              do j = 1,n
                  if (beta.eq.zero) then
                      do i = 1,m
                          c(i,j) = zero
                      enddo
                  elseif (beta.ne.one) then
                      do i = 1,m
                          c(i,j) = beta*c(i,j)
                      enddo
                  endif
                  do l = 1,k
                      temp = alpha*conjg(b(j,l))
                      do i = 1,m
                          c(i,j) = c(i,j) + temp*a(i,l)
                      enddo
                  enddo
              enddo
          else
!
!           Form  C := alpha*A*B**T + beta*C
!
              do j = 1,n
                  if (beta.eq.zero) then
                      do i = 1,m
                          c(i,j) = zero
                      enddo
                  elseif (beta.ne.one) then
                      do i = 1,m
                          c(i,j) = beta*c(i,j)
                      enddo
                  endif
                  do l = 1,k
                      temp = alpha*b(j,l)
                      do i = 1,m
                          c(i,j) = c(i,j) + temp*a(i,l)
                      enddo
                  enddo
              enddo
          endif
      elseif (conja) then
          if (conjb) then
!
!           Form  C := alpha*A**H*B**H + beta*C.
!
              do j = 1,n
                  do i = 1,m
                      temp = zero
                      do l = 1,k
                          temp = temp + conjg(a(l,i))*conjg(b(j,l))
                      enddo
                      if (beta.eq.zero) then
                          c(i,j) = alpha*temp
                      else
                          c(i,j) = alpha*temp + beta*c(i,j)
                      endif
                  enddo
              enddo
          else
!
!           Form  C := alpha*A**H*B**T + beta*C
!
              do j = 1,n
                  do i = 1,m
                      temp = zero
                      do l = 1,k
                          temp = temp + conjg(a(l,i))*b(j,l)
                      enddo
                      if (beta.eq.zero) then
                          c(i,j) = alpha*temp
                      else
                          c(i,j) = alpha*temp + beta*c(i,j)
                      endif
                  enddo
              enddo
          endif
      else
          if (conjb) then
!
!           Form  C := alpha*A**T*B**H + beta*C
!
              do j = 1,n
                  do i = 1,m
                      temp = zero
                      do l = 1,k
                          temp = temp + a(l,i)*conjg(b(j,l))
                      enddo
                      if (beta.eq.zero) then
                          c(i,j) = alpha*temp
                      else
                          c(i,j) = alpha*temp + beta*c(i,j)
                      endif
                  enddo
              enddo
          else
!
!           Form  C := alpha*A**T*B**T + beta*C
!
              do j = 1,n
                  do i = 1,m
                      temp = zero
                      do l = 1,k
                          temp = temp + a(l,i)*b(j,l)
                      enddo
                      if (beta.eq.zero) then
                          c(i,j) = alpha*temp
                      else
                          c(i,j) = alpha*temp + beta*c(i,j)
                      endif
                  enddo
              enddo
          endif
      endif
!
!     End of CGEMM .
!
      end subroutine cgemm
