      integer i,info,ix,j,jx,k,kk,kx
      logical noconj,nounit
!     ..
!     .. External Functions ..
!     ..
!     .. External Subroutines ..
!     ..
!     .. Intrinsic Functions ..
      intrinsic conjg
!     ..
!
!     Test the input parameters.
!
      info = 0
      if (.not.lsame(uplo,'U') .and. .not.lsame(uplo,'L')) then
          info = 1
      elseif (.not.lsame(trans,'N') .and. .not.lsame(trans,'T') .and.  .not.lsame(trans,'C')) then
          info = 2
      elseif (.not.lsame(diag,'U') .and. .not.lsame(diag,'N')) then
          info = 3
      elseif (n.lt.0) then
          info = 4
      elseif (incx.eq.0) then
          info = 7
      endif
      if (info.ne.0) then
          call xerbla('CTPMV ',info)
          return
      endif
!
!     Quick return if possible.
!
      if (n.eq.0) return
!
      noconj = lsame(trans,'T')
      nounit = lsame(diag,'N')
!
!     Set up the start point in X if the increment is not unity. This
!     will be  ( N - 1 )*INCX  too small for descending loops.
!
      if (incx.le.0) then
          kx = 1 - (n-1)*incx
      elseif (incx.ne.1) then
          kx = 1
      endif
!
!     Start the operations. In this version the elements of AP are
!     accessed sequentially with one pass through AP.
!
      if (lsame(trans,'N')) then
!
!        Form  x:= A*x.
!
          if (lsame(uplo,'U')) then
              kk = 1
              if (incx.eq.1) then
                  do j = 1,n
                      if (x(j).ne.zero) then
                          temp = x(j)
                          k = kk
                          do i = 1,j - 1
                              x(i) = x(i) + temp*ap(k)
                              k = k + 1
                          enddo
                          if (nounit) x(j) = x(j)*ap(kk+j-1)
                      endif
                      kk = kk + j
                  enddo
              else
                  jx = kx
                  do j = 1,n
                      if (x(jx).ne.zero) then
                          temp = x(jx)
                          ix = kx
                          do k = kk,kk + j - 2
                              x(ix) = x(ix) + temp*ap(k)
                              ix = ix + incx
                          enddo
                          if (nounit) x(jx) = x(jx)*ap(kk+j-1)
                      endif
                      jx = jx + incx
                      kk = kk + j
                  enddo
              endif
          else
              kk = (n* (n+1))/2
              if (incx.eq.1) then
                  do j = n,1,-1
                      if (x(j).ne.zero) then
                          temp = x(j)
                          k = kk
                          do i = n,j + 1,-1
                              x(i) = x(i) + temp*ap(k)
                              k = k - 1
                          enddo
                          if (nounit) x(j) = x(j)*ap(kk-n+j)
                      endif
                      kk = kk - (n-j+1)
                  enddo
              else
                  kx = kx + (n-1)*incx
                  jx = kx
                  do j = n,1,-1
                      if (x(jx).ne.zero) then
                          temp = x(jx)
                          ix = kx
                          do k = kk,kk - (n- (j+1)),-1
                              x(ix) = x(ix) + temp*ap(k)
                              ix = ix - incx
                          enddo
                          if (nounit) x(jx) = x(jx)*ap(kk-n+j)
                      endif
                      jx = jx - incx
                      kk = kk - (n-j+1)
                  enddo
              endif
          endif
      else
!
!        Form  x := A**T*x  or  x := A**H*x.
!
          if (lsame(uplo,'U')) then
              kk = (n* (n+1))/2
              if (incx.eq.1) then
                  do j = n,1,-1
                      temp = x(j)
                      k = kk - 1
                      if (noconj) then
                          if (nounit) temp = temp*ap(kk)
                          do i = j - 1,1,-1
                              temp = temp + ap(k)*x(i)
                              k = k - 1
                          enddo
                      else
                          if (nounit) temp = temp*conjg(ap(kk))
                          do i = j - 1,1,-1
                              temp = temp + conjg(ap(k))*x(i)
                              k = k - 1
                          enddo
                      endif
                      x(j) = temp
                      kk = kk - j
                  enddo
              else
                  jx = kx + (n-1)*incx
                  do j = n,1,-1
                      temp = x(jx)
                      ix = jx
                      if (noconj) then
                          if (nounit) temp = temp*ap(kk)
                          do k = kk - 1,kk - j + 1,-1
                              ix = ix - incx
                              temp = temp + ap(k)*x(ix)
                          enddo
                      else
                          if (nounit) temp = temp*conjg(ap(kk))
                          do k = kk - 1,kk - j + 1,-1
                              ix = ix - incx
                              temp = temp + conjg(ap(k))*x(ix)
                          enddo
                      endif
                      x(jx) = temp
                      jx = jx - incx
                      kk = kk - j
                  enddo
              endif
          else
              kk = 1
              if (incx.eq.1) then
                  do j = 1,n
                      temp = x(j)
                      k = kk + 1
                      if (noconj) then
                          if (nounit) temp = temp*ap(kk)
                          do i = j + 1,n
                              temp = temp + ap(k)*x(i)
                              k = k + 1
                          enddo
                      else
                          if (nounit) temp = temp*conjg(ap(kk))
                          do i = j + 1,n
                              temp = temp + conjg(ap(k))*x(i)
                              k = k + 1
                          enddo
                      endif
                      x(j) = temp
                      kk = kk + (n-j+1)
                  enddo
              else
                  jx = kx
                  do j = 1,n
                      temp = x(jx)
                      ix = jx
                      if (noconj) then
                          if (nounit) temp = temp*ap(kk)
                          do k = kk + 1,kk + n - j
                              ix = ix + incx
                              temp = temp + ap(k)*x(ix)
                          enddo
                      else
                          if (nounit) temp = temp*conjg(ap(kk))
                          do k = kk + 1,kk + n - j
                              ix = ix + incx
                              temp = temp + conjg(ap(k))*x(ix)
                          enddo
                      endif
                      x(jx) = temp
                      jx = jx + incx
                      kk = kk + (n-j+1)
                  enddo
              endif
          endif
      endif
!
!     End of CTPMV .
!
      end subroutine ctpmv
