                              ix = ix + incx
                          enddo
                          if (nounit) temp = temp/ap(kk+j-1)
                      else
                          do k = kk,kk + j - 2
                              temp = temp - conjg(ap(k))*x(ix)
                              ix = ix + incx
                          enddo
                          if (nounit) temp = temp/conjg(ap(kk+j-1))
                      endif
