                              temp = temp - conjg(ap(k))*x(i)
                              k = k - 1
                          enddo
                          if (nounit) temp = temp/conjg(ap(kk-n+j))
                      endif
                      x(j) = temp
                      kk = kk - (n-j+1)
                  enddo
              else
                  kx = kx + (n-1)*incx
                  jx = kx
                  do j = n,1,-1
                      temp = x(jx)
                      ix = kx
                      if (noconj) then
                          do k = kk,kk - (n- (j+1)),-1
                              temp = temp - ap(k)*x(ix)
                              ix = ix - incx
                          enddo
                          if (nounit) temp = temp/ap(kk-n+j)
                      else
                          do k = kk,kk - (n- (j+1)),-1
                              temp = temp - conjg(ap(k))*x(ix)
                              ix = ix - incx
                          enddo
                          if (nounit) temp = temp/conjg(ap(kk-n+j))
                      endif
                      x(jx) = temp
                      jx = jx - incx
                      kk = kk - (n-j+1)
                  enddo
              endif
          endif
      endif
