!     .. Local Scalars ..
      complex temp
      integer i,info,j,k,nrowa
      logical lside,noconj,nounit,upper
!     ..
!     .. Parameters ..
      complex one
      parameter (one= (1.0e+0,0.0e+0))
      complex zero
      parameter (zero= (0.0e+0,0.0e+0))
!     ..
!
!     Test the input parameters.
!
      lside = lsame(side,'L')
      if (lside) then
          nrowa = m
      else
          nrowa = n
      endif
      noconj = lsame(transa,'T')
      nounit = lsame(diag,'N')
      upper = lsame(uplo,'U')
!
      info = 0
      if ((.not.lside) .and. (.not.lsame(side,'R'))) then
          info = 1
      elseif ((.not.upper) .and. (.not.lsame(uplo,'L'))) then
          info = 2
      elseif ((.not.lsame(transa,'N')) .and.  (.not.lsame(transa,'T')) .and.  (.not.lsame(transa,'C'))) then
          info = 3
      elseif ((.not.lsame(diag,'U')) .and. (.not.lsame(diag,'N'))) then
          info = 4
      elseif (m.lt.0) then
          info = 5
      elseif (n.lt.0) then
          info = 6
      elseif (lda.lt.max(1,nrowa)) then
          info = 9
      elseif (ldb.lt.max(1,m)) then
          info = 11
      endif
      if (info.ne.0) then
          call xerbla('CTRMM ',info)
          return
      endif
!
!     Quick return if possible.
!
      if (m.eq.0 .or. n.eq.0) return
!
!     And when  alpha.eq.zero.
!
      if (alpha.eq.zero) then
          b(1:m,1:n) = zero
          return
      endif
!
!     Start the operations.
!
      if (lside) then
          if (lsame(transa,'N')) then
!
!           Form  B := alpha*A*B.
!
              if (upper) then
                  do j = 1,n
                      do k = 1,m
                          if (b(k,j).ne.zero) then
                              temp = alpha*b(k,j)
                              do i = 1,k - 1
                                  b(i,j) = b(i,j) + temp*a(i,k)
                              enddo
                              if (nounit) temp = temp*a(k,k)
                              b(k,j) = temp
                          endif
                      enddo
                  enddo
              else
                  do j = 1,n
                      do k = m,1,-1
                          if (b(k,j).ne.zero) then
                              temp = alpha*b(k,j)
                              b(k,j) = temp
                              if (nounit) b(k,j) = b(k,j)*a(k,k)
                              do i = k + 1,m
                                  b(i,j) = b(i,j) + temp*a(i,k)
                              enddo
                          endif
                      enddo
                  enddo
              endif
          else
!
!           Form  B := alpha*A**T*B   or   B := alpha*A**H*B.
!
              if (upper) then
                  do j = 1,n
                      do i = m,1,-1
                          temp = b(i,j)
                          if (noconj) then
                              if (nounit) temp = temp*a(i,i)
                              do k = 1,i - 1
                                  temp = temp + a(k,i)*b(k,j)
                              enddo
                          else
                              if (nounit) temp = temp*conjg(a(i,i))
                              do k = 1,i - 1
                                  temp = temp + conjg(a(k,i))*b(k,j)
                              enddo
                          endif
                          b(i,j) = alpha*temp
                      enddo
                  enddo
              else
                  do j = 1,n
                      do i = 1,m
                          temp = b(i,j)
                          if (noconj) then
                              if (nounit) temp = temp*a(i,i)
                              do k = i + 1,m
                                  temp = temp + a(k,i)*b(k,j)
                              enddo
                          else
                              if (nounit) temp = temp*conjg(a(i,i))
                              do k = i + 1,m
                                  temp = temp + conjg(a(k,i))*b(k,j)
                              enddo
                          endif
                          b(i,j) = alpha*temp
                      enddo
                  enddo
              endif
          endif
      else
          if (lsame(transa,'N')) then
!
!           Form  B := alpha*B*A.
!
              if (upper) then
                  do j = n,1,-1
                      temp = alpha
                      if (nounit) temp = temp*a(j,j)
                      b(1:m,j) = temp*b(1:m,j)
                      do k = 1,j - 1
                          if (a(k,j).ne.zero) then
                              temp = alpha*a(k,j)
                              b(1:m,j) = b(1:m,j) + temp*b(1:m,k)
                          endif
                      enddo
                  enddo
              else
                  do j = 1,n
                      temp = alpha
                      if (nounit) temp = temp*a(j,j)
                      b(1:m,j) = temp*b(1:m,j)
                      do k = j + 1,n
                          if (a(k,j).ne.zero) then
                              temp = alpha*a(k,j)
                              b(1:m,j) = b(1:m,j) + temp*b(1:m,k)
                          endif
                      enddo
                  enddo
              endif
          else
!
!           Form  B := alpha*B*A**T   or   B := alpha*B*A**H.
!
              if (upper) then
                  do k = 1,n
                      do j = 1,k - 1
                          if (a(j,k).ne.zero) then
                              if (noconj) then
                                  temp = alpha*a(j,k)
                              else
                                  temp = alpha*conjg(a(j,k))
                              endif
                              b(1:m,j) = b(1:m,j) + temp*b(1:m,k)
                          endif
                      enddo
                      temp = alpha
                      if (nounit) then
                          if (noconj) then
                              temp = temp*a(k,k)
                          else
                              temp = temp*conjg(a(k,k))
                          endif
                      endif
                      if (temp.ne.one) then
                          b(1:m,k) = temp*b(1:m,k)
                      endif
                  enddo
              else
                  do k = n,1,-1
                      do j = k + 1,n
                          if (a(j,k).ne.zero) then
                              if (noconj) then
                                  temp = alpha*a(j,k)
                              else
                                  temp = alpha*conjg(a(j,k))
                              endif
                              b(1:m,j) = b(1:m,j) + temp*b(1:m,k)
                          endif
                      enddo
                      temp = alpha
                      if (nounit) then
                          if (noconj) then
                              temp = temp*a(k,k)
                          else
                              temp = temp*conjg(a(k,k))
                          endif
                      endif
                      if (temp.ne.one) then
                          b(1:m,k) = temp*b(1:m,k)
                      endif
                  enddo
              endif
          endif
      endif
!
!     End of CTRMM .
!
      end subroutine ctrmm
