                          IF (NOUNIT) X(JX) = X(JX)/AP(KK)
                          TEMP = X(JX)
                          IX = JX
                          DO K = KK + 1,KK + N - J
                              IX = IX + INCX
                              X(IX) = X(IX) - TEMP*AP(K)
                          enddo
                      endif
                      JX = JX + INCX
                      KK = KK + (N-J+1)
                  enddo
              endif
          endif
      ELSE
!
!        Form  x := inv( A**T )*x.
!
          IF (LSAME(UPLO,'U')) THEN
              KK = 1
              IF (INCX.EQ.1) THEN
                  DO J = 1,N
                      TEMP = X(J)
                      K = KK
                      DO I = 1,J - 1
                          TEMP = TEMP - AP(K)*X(I)
                          K = K + 1
                      enddo
                      IF (NOUNIT) TEMP = TEMP/AP(KK+J-1)
                      X(J) = TEMP
                      KK = KK + J
                  enddo
              ELSE
                  JX = KX
                  DO J = 1,N
                      TEMP = X(JX)
                      IX = KX
                      DO K = KK,KK + J - 2
                          TEMP = TEMP - AP(K)*X(IX)
                          IX = IX + INCX
                      enddo
                      IF (NOUNIT) TEMP = TEMP/AP(KK+J-1)
                      X(JX) = TEMP
                      JX = JX + INCX
                      KK = KK + J
                  enddo
              endif
          ELSE
              KK = (N* (N+1))/2
              IF (INCX.EQ.1) THEN
                  DO J = N,1,-1
                      TEMP = X(J)
                      K = KK
                      DO I = N,J + 1,-1
                          TEMP = TEMP - AP(K)*X(I)
                          K = K - 1
                      enddo
                      IF (NOUNIT) TEMP = TEMP/AP(KK-N+J)
                      X(J) = TEMP
                      KK = KK - (N-J+1)
                  enddo
              ELSE
                  KX = KX + (N-1)*INCX
                  JX = KX
                  DO J = N,1,-1
                      TEMP = X(JX)
                      IX = KX
                      DO K = KK,KK - (N- (J+1)),-1
                          TEMP = TEMP - AP(K)*X(IX)
                          IX = IX - INCX
                      enddo
                      IF (NOUNIT) TEMP = TEMP/AP(KK-N+J)
                      X(JX) = TEMP
                      JX = JX - INCX
                      KK = KK - (N-J+1)
                  enddo
              endif
          endif
      endif
!
      END SUBROUTINE DTPSV
