!!##NAME
!!    srotm(3f) - [BLAS:SINGLE_BLAS_LEVEL1] Apply a modified Given's rotation.
!!
!!##SYNOPSIS
!!
!!     SUBROUTINE SROTM(N,SX,INCX,SY,INCY,SPARAM)
!!
!!       .. Scalar Arguments ..
!!       INTEGER,intent(in) :: INCX,INCY,N
!!       ..
!!       .. Array Arguments ..
!!       REAL,intent(in)    :: SPARAM(5)
!!       REAL,intent(inout) :: SX(*),SY(*)
!!       ..
!!
!!##DEFINITION
!!
!!     APPLY THE MODIFIED GIVENS TRANSFORMATION, H, TO THE 2 BY N MATRIX
!!
!!     (SX**T) , WHERE **T INDICATES TRANSPOSE. THE ELEMENTS OF SX ARE IN
!!     (SX**T)
!!
!!     SX(LX+I*INCX), I = 0 TO N-1, WHERE LX = 1 IF INCX .GE. 0, ELSE
!!     LX = (-INCX)*N, AND SIMILARLY FOR SY USING USING LY AND INCY.
!!     WITH SPARAM(1)=SFLAG, H HAS ONE OF THE FOLLOWING FORMS..
!!
!!        SFLAG=-1.E0     SFLAG=0.E0        SFLAG=1.E0     SFLAG=-2.E0
!!
!!          (SH11  SH12)    (1.E0  SH12)    (SH11  1.E0)    (1.E0  0.E0)
!!        H=(          )    (          )    (          )    (          )
!!          (SH21  SH22),   (SH21  1.E0),   (-1.E0 SH22),   (0.E0  1.E0).
!!
!!     SEE  SROTMG FOR A DESCRIPTION OF DATA STORAGE IN SPARAM.
!!
!!##OPTIONS
!!
!!   N
!!
!!           N is INTEGER
!!          number of elements in input vector(s)
!!
!!  SX
!!
!!           SX is REAL array, dimension ( 1 + ( N - 1 )*abs( INCX ) )
!!
!!   INCX
!!
!!           INCX is INTEGER
!!          storage spacing between elements of SX
!!
!!  SY
!!
!!           SY is REAL array, dimension ( 1 + ( N - 1 )*abs( INCY ) )
!!
!!   INCY
!!
!!           INCY is INTEGER
!!          storage spacing between elements of SY
!!
!!   SPARAM
!!
!!           SPARAM is REAL array, dimension (5)
!!      SPARAM(1)=SFLAG
!!      SPARAM(2)=SH11
!!      SPARAM(3)=SH21
!!      SPARAM(4)=SH12
!!      SPARAM(5)=SH22
!!
!!##AUTHORS
!!
!! + Univ. of Tennessee
!! + Univ. of California Berkeley
!! + Univ. of Colorado Denver
!! + NAG Ltd.
!!
!!  date:November 2017
!!
!!##SEE ALSO
!!    Online html documentation available at
!!    http://www.netlib.org/lapack/explore-html/
!  =====================================================================
       SUBROUTINE SROTM(N,SX,INCX,SY,INCY,SPARAM)
      implicit none
!
!  -- Reference BLAS level1 routine (version 3.8.0) --
!  -- Reference BLAS is a software package provided by Univ. of Tennessee,    --
!  -- Univ. of California Berkeley, Univ. of Colorado Denver and NAG Ltd..--
!     November 2017
!
!     .. Scalar Arguments ..
      INTEGER,intent(in) :: INCX,INCY,N
!     ..
!     .. Array Arguments ..
      REAL,intent(in)    :: SPARAM(5)
      REAL,intent(inout) :: SX(*),SY(*)
!     ..
!
!  =====================================================================
!
!     .. Local Scalars ..
      REAL SFLAG,SH11,SH12,SH21,SH22,W,Z
      real,parameter :: zero=0.0e0
      real,parameter :: two=2.0e0
      INTEGER I,KX,KY,NSTEPS
!     ..
!
      SFLAG = SPARAM(1)
      IF (N.LE.0 .OR. (SFLAG+TWO.EQ.ZERO)) RETURN
      IF (INCX.EQ.INCY.AND.INCX.GT.0) THEN
!
         NSTEPS = N*INCX
         IF (SFLAG.LT.ZERO) THEN
            SH11 = SPARAM(2)
            SH12 = SPARAM(4)
            SH21 = SPARAM(3)
            SH22 = SPARAM(5)
            DO I = 1,NSTEPS,INCX
               W = SX(I)
               Z = SY(I)
               SX(I) = W*SH11 + Z*SH12
               SY(I) = W*SH21 + Z*SH22
            enddo
         elseif (SFLAG.EQ.ZERO) THEN
            SH12 = SPARAM(4)
            SH21 = SPARAM(3)
            DO I = 1,NSTEPS,INCX
               W = SX(I)
               Z = SY(I)
               SX(I) = W + Z*SH12
               SY(I) = W*SH21 + Z
            enddo
         ELSE
            SH11 = SPARAM(2)
            SH22 = SPARAM(5)
            DO I = 1,NSTEPS,INCX
               W = SX(I)
               Z = SY(I)
               SX(I) = W*SH11 + Z
               SY(I) = -W + SH22*Z
            enddo
         endif
      ELSE
         KX = 1
         KY = 1
         IF (INCX.LT.0) KX = 1 + (1-N)*INCX
         IF (INCY.LT.0) KY = 1 + (1-N)*INCY
!
         IF (SFLAG.LT.ZERO) THEN
            SH11 = SPARAM(2)
            SH12 = SPARAM(4)
            SH21 = SPARAM(3)
            SH22 = SPARAM(5)
            DO I = 1,N
               W = SX(KX)
               Z = SY(KY)
               SX(KX) = W*SH11 + Z*SH12
               SY(KY) = W*SH21 + Z*SH22
               KX = KX + INCX
               KY = KY + INCY
            enddo
         elseif (SFLAG.EQ.ZERO) THEN
            SH12 = SPARAM(4)
            SH21 = SPARAM(3)
            DO I = 1,N
               W = SX(KX)
               Z = SY(KY)
               SX(KX) = W + Z*SH12
               SY(KY) = W*SH21 + Z
               KX = KX + INCX
               KY = KY + INCY
            enddo
         ELSE
             SH11 = SPARAM(2)
             SH22 = SPARAM(5)
             DO I = 1,N
                W = SX(KX)
                Z = SY(KY)
                SX(KX) = W*SH11 + Z
                SY(KY) = -W + SH22*Z
                KX = KX + INCX
                KY = KY + INCY
            enddo
         endif
      endif

      END SUBROUTINE SROTM
