                          IX = IX + INCX
                          IY = IY + INCY
                      enddo
                  endif
                  JX = JX + INCX
                  JY = JY + INCY
                  KK = KK + J
              enddo
          endif
      ELSE
!
!        Form  A  when lower triangle is stored in AP.
!
          IF ((INCX.EQ.1) .AND. (INCY.EQ.1)) THEN
              DO J = 1,N
                  IF ((X(J).NE.ZERO) .OR. (Y(J).NE.ZERO)) THEN
                      TEMP1 = ALPHA*Y(J)
                      TEMP2 = ALPHA*X(J)
                      K = KK
                      DO I = J,N
                          AP(K) = AP(K) + X(I)*TEMP1 + Y(I)*TEMP2
                          K = K + 1
                      enddo
                  endif
                  KK = KK + N - J + 1
              enddo
          ELSE
              DO J = 1,N
                  IF ((X(JX).NE.ZERO) .OR. (Y(JY).NE.ZERO)) THEN
                      TEMP1 = ALPHA*Y(JY)
                      TEMP2 = ALPHA*X(JX)
                      IX = JX
                      IY = JY
                      DO K = KK,KK + N - J
                          AP(K) = AP(K) + X(IX)*TEMP1 + Y(IY)*TEMP2
                          IX = IX + INCX
                          IY = IY + INCY
                      enddo
                  endif
                  JX = JX + INCX
                  JY = JY + INCY
                  KK = KK + N - J + 1
              enddo
          endif
      endif

      END SUBROUTINE SSPR2
