                  enddo
              endif
          ELSE
              KK = (N* (N+1))/2
              IF (INCX.EQ.1) THEN
                  DO J = N,1,-1
                      IF (X(J).NE.ZERO) THEN
                          TEMP = X(J)
                          K = KK
                          DO I = N,J + 1,-1
                              X(I) = X(I) + TEMP*AP(K)
                              K = K - 1
                          enddo
                          IF (NOUNIT) X(J) = X(J)*AP(KK-N+J)
                      endif
                      KK = KK - (N-J+1)
                  enddo
              ELSE
                  KX = KX + (N-1)*INCX
                  JX = KX
                  DO J = N,1,-1
                      IF (X(JX).NE.ZERO) THEN
                          TEMP = X(JX)
                          IX = KX
                          DO K = KK,KK - (N- (J+1)),-1
                              X(IX) = X(IX) + TEMP*AP(K)
                              IX = IX - INCX
                          enddo
                          IF (NOUNIT) X(JX) = X(JX)*AP(KK-N+J)
                      endif
                      JX = JX - INCX
                      KK = KK - (N-J+1)
                  enddo
              endif
          endif
      ELSE
!
!        Form  x := A**T*x.
!
          IF (LSAME(UPLO,'U')) THEN
              KK = (N* (N+1))/2
              IF (INCX.EQ.1) THEN
                  DO J = N,1,-1
                      TEMP = X(J)
                      IF (NOUNIT) TEMP = TEMP*AP(KK)
                      K = KK - 1
                      DO I = J - 1,1,-1
                          TEMP = TEMP + AP(K)*X(I)
                          K = K - 1
                      enddo
                      X(J) = TEMP
                      KK = KK - J
                  enddo
              ELSE
                  JX = KX + (N-1)*INCX
                  DO J = N,1,-1
                      TEMP = X(JX)
                      IX = JX
                      IF (NOUNIT) TEMP = TEMP*AP(KK)
                      DO K = KK - 1,KK - J + 1,-1
                          IX = IX - INCX
                          TEMP = TEMP + AP(K)*X(IX)
                      enddo
                      X(JX) = TEMP
                      JX = JX - INCX
                      KK = KK - J
                  enddo
              endif
          ELSE
              KK = 1
              IF (INCX.EQ.1) THEN
                  DO J = 1,N
                      TEMP = X(J)
                      IF (NOUNIT) TEMP = TEMP*AP(KK)
                      K = KK + 1
                      DO I = J + 1,N
                          TEMP = TEMP + AP(K)*X(I)
                          K = K + 1
                      enddo
                      X(J) = TEMP
                      KK = KK + (N-J+1)
                  enddo
              ELSE
                  JX = KX
                  DO J = 1,N
                      TEMP = X(JX)
                      IX = JX
                      IF (NOUNIT) TEMP = TEMP*AP(KK)
                      DO K = KK + 1,KK + N - J
                          IX = IX + INCX
                          TEMP = TEMP + AP(K)*X(IX)
                      enddo
                      X(JX) = TEMP
                      JX = JX + INCX
                      KK = KK + (N-J+1)
                  enddo
              endif
          endif
      endif

      END SUBROUTINE STPMV
