!!    http://www.netlib.org/lapack/explore-html/
!  =====================================================================
       SUBROUTINE ZHERK(UPLO,TRANS,N,K,ALPHA,A,LDA,BETA,C,LDC)
      implicit none
!
!  -- Reference BLAS level3 routine (version 3.7.0) --
!  -- Reference BLAS is a software package provided by Univ. of Tennessee,    --
!  -- Univ. of California Berkeley, Univ. of Colorado Denver and NAG Ltd..--
!     December 2016
!
!     .. Scalar Arguments ..
      DOUBLE PRECISION,intent(in)        :: ALPHA,BETA
      INTEGER,intent(in)                 :: K,LDA,LDC,N
      CHARACTER,intent(in)               :: TRANS,UPLO
!     ..
!     .. Array Arguments ..
      complex(kind=real64),intent(in)    :: A(LDA,*)
      complex(kind=real64),intent(inout) :: C(LDC,*)
!     ..
!
!  =====================================================================
!
!     .. External Functions ..
!      LOGICAL LSAME
!      EXTERNAL LSAME
!     ..
!     .. External Subroutines ..
!      EXTERNAL XERBLA
!     ..
!     .. Intrinsic Functions ..
      INTRINSIC DBLE,DCMPLX,DCONJG,MAX
!     ..
!     .. Local Scalars ..
      complex(kind=real64) :: TEMP
      DOUBLE PRECISION RTEMP
      INTEGER I,INFO,J,L,NROWA
      LOGICAL UPPER
!     ..
!     .. Parameters ..
      DOUBLE PRECISION ONE,ZERO
      PARAMETER (ONE=1.0D+0,ZERO=0.0D+0)
!     ..
!
!     Test the input parameters.
!
      IF (LSAME(TRANS,'N')) THEN
          NROWA = N
      ELSE
          NROWA = K
      endif
      UPPER = LSAME(UPLO,'U')
!
      INFO = 0
      IF ((.NOT.UPPER) .AND. (.NOT.LSAME(UPLO,'L'))) THEN
          INFO = 1
      ELSEIF ((.NOT.LSAME(TRANS,'N')) .AND.  (.NOT.LSAME(TRANS,'C'))) THEN
          INFO = 2
      ELSEIF (N.LT.0) THEN
          INFO = 3
      ELSEIF (K.LT.0) THEN
          INFO = 4
      ELSEIF (LDA.LT.MAX(1,NROWA)) THEN
          INFO = 7
      ELSEIF (LDC.LT.MAX(1,N)) THEN
          INFO = 10
      endif
      IF (INFO.NE.0) THEN
          CALL XERBLA('ZHERK ',INFO)
          RETURN
      endif
!
!     Quick return if possible.
!
      IF ((N.EQ.0) .OR. (((ALPHA.EQ.ZERO).OR.  (K.EQ.0)).AND. (BETA.EQ.ONE))) RETURN
!
!     And when  alpha.eq.zero.
!
      IF (ALPHA.EQ.ZERO) THEN
          IF (UPPER) THEN
              IF (BETA.EQ.ZERO) THEN
                  DO J = 1,N
                      C(1:J,J) = ZERO
                  enddo
              ELSE
                  DO J = 1,N
                      DO I = 1,J - 1
                          C(I,J) = BETA*C(I,J)
                      enddo
                      C(J,J) = BETA*DBLE(C(J,J))
                  enddo
              endif
          ELSE
              IF (BETA.EQ.ZERO) THEN
                  DO J = 1,N
                      C(J:N,J) = ZERO
                  enddo
              ELSE
                  DO J = 1,N
                      C(J,J) = BETA*DBLE(C(J,J))
                      DO I = J + 1,N
                          C(I,J) = BETA*C(I,J)
                      enddo
                  enddo
              endif
          endif
          RETURN
      endif
!
!     Start the operations.
!
      IF (LSAME(TRANS,'N')) THEN
!
!        Form  C := alpha*A*A**H + beta*C.
!
          IF (UPPER) THEN
              DO J = 1,N
                  IF (BETA.EQ.ZERO) THEN
                       C(1:J,J) = ZERO
                  ELSEIF (BETA.NE.ONE) THEN
                      DO I = 1,J - 1
                          C(I,J) = BETA*C(I,J)
                      enddo
                      C(J,J) = BETA*DBLE(C(J,J))
                  ELSE
                      C(J,J) = DBLE(C(J,J))
                  endif
                  DO L = 1,K
                      IF (A(J,L).NE.DCMPLX(ZERO)) THEN
                          TEMP = ALPHA*DCONJG(A(J,L))
                          DO I = 1,J - 1
                              C(I,J) = C(I,J) + TEMP*A(I,L)
                          enddo
                          C(J,J) = DBLE(C(J,J)) + DBLE(TEMP*A(I,L))
                      endif
                  enddo
              enddo
          ELSE
              DO J = 1,N
                  IF (BETA.EQ.ZERO) THEN
                      C(J:N,J) = ZERO
                  ELSEIF (BETA.NE.ONE) THEN
                      C(J,J) = BETA*DBLE(C(J,J))
                      DO I = J + 1,N
                          C(I,J) = BETA*C(I,J)
                      enddo
                  ELSE
                      C(J,J) = DBLE(C(J,J))
                  endif
                  DO L = 1,K
                      IF (A(J,L).NE.DCMPLX(ZERO)) THEN
                          TEMP = ALPHA*DCONJG(A(J,L))
                          C(J,J) = DBLE(C(J,J)) + DBLE(TEMP*A(J,L))
                          DO I = J + 1,N
                              C(I,J) = C(I,J) + TEMP*A(I,L)
                          enddo
                      endif
                  enddo
              enddo
          endif
      ELSE
!
!        Form  C := alpha*A**H*A + beta*C.
!
          IF (UPPER) THEN
              DO J = 1,N
                  DO I = 1,J - 1
                      TEMP = ZERO
                      DO L = 1,K
                          TEMP = TEMP + DCONJG(A(L,I))*A(L,J)
                      enddo
                      IF (BETA.EQ.ZERO) THEN
                          C(I,J) = ALPHA*TEMP
                      ELSE
                          C(I,J) = ALPHA*TEMP + BETA*C(I,J)
                      endif
                  enddo
                  RTEMP = ZERO
                  DO L = 1,K
                      RTEMP = RTEMP + DCONJG(A(L,J))*A(L,J)
                  enddo
                  IF (BETA.EQ.ZERO) THEN
                      C(J,J) = ALPHA*RTEMP
                  ELSE
                      C(J,J) = ALPHA*RTEMP + BETA*DBLE(C(J,J))
                  endif
              enddo
          ELSE
              DO J = 1,N
                  RTEMP = ZERO
                  DO L = 1,K
                      RTEMP = RTEMP + DCONJG(A(L,J))*A(L,J)
                  enddo
                  IF (BETA.EQ.ZERO) THEN
                      C(J,J) = ALPHA*RTEMP
                  ELSE
                      C(J,J) = ALPHA*RTEMP + BETA*DBLE(C(J,J))
                  endif
                  DO I = J + 1,N
                      TEMP = ZERO
                      DO L = 1,K
                          TEMP = TEMP + DCONJG(A(L,I))*A(L,J)
                      enddo
                      IF (BETA.EQ.ZERO) THEN
                          C(I,J) = ALPHA*TEMP
                      ELSE
                          C(I,J) = ALPHA*TEMP + BETA*C(I,J)
                      endif
                  enddo
              enddo
          endif
      endif

      END SUBROUTINE ZHERK
