!        Form  C := alpha*B*A + beta*C.
!
          DO J = 1,N
              TEMP1 = ALPHA*A(J,J)
              IF (BETA.EQ.ZERO) THEN
                  C(1:M,J) = TEMP1*B(1:M,J)
              ELSE
                  C(1:M,J) = BETA*C(1:M,J) + TEMP1*B(1:M,J)
              endif
              DO K = 1,J - 1
                  IF (UPPER) THEN
                      TEMP1 = ALPHA*A(K,J)
                  ELSE
                      TEMP1 = ALPHA*A(J,K)
                  endif
                  C(1:M,J) = C(1:M,J) + TEMP1*B(1:M,K)
              enddo
              DO K = J + 1,N
                  IF (UPPER) THEN
                      TEMP1 = ALPHA*A(J,K)
                  ELSE
                      TEMP1 = ALPHA*A(K,J)
                  endif
                  C(1:M,J) = C(1:M,J) + TEMP1*B(1:M,K)
              enddo
          enddo
      endif

