       SUBROUTINE ZTPMV(UPLO,TRANS,DIAG,N,AP,X,INCX)
      implicit none
!
!  -- Reference BLAS level2 routine (version 3.7.0) --
!  -- Reference BLAS is a software package provided by Univ. of Tennessee,    --
!  -- Univ. of California Berkeley, Univ. of Colorado Denver and NAG Ltd..--
!     December 2016
!
!     .. Scalar Arguments ..
      INTEGER,intent(in)                 :: INCX,N
      CHARACTER,intent(in)               :: DIAG,TRANS,UPLO
!     ..
!     .. Array Arguments ..
      complex(kind=real64),intent(in)    :: AP(*)
      complex(kind=real64),intent(inout) :: X(*)
!     ..
!
!  =====================================================================
!
!     .. Parameters ..
      complex(kind=real64) :: ZERO
      PARAMETER (ZERO= (0.0D+0,0.0D+0))
!     ..
!     .. Local Scalars ..
      complex(kind=real64) :: TEMP
      INTEGER I,INFO,IX,J,JX,K,KK,KX
      LOGICAL NOCONJ,NOUNIT
!     ..
!     .. External Functions ..
!      LOGICAL LSAME
!      EXTERNAL LSAME
!     ..
!     .. External Subroutines ..
!      EXTERNAL XERBLA
!     ..
!     .. Intrinsic Functions ..
      INTRINSIC DCONJG
!     ..
!
!     Test the input parameters.
!
      INFO = 0
      IF (.NOT.LSAME(UPLO,'U') .AND. .NOT.LSAME(UPLO,'L')) THEN
          INFO = 1
      elseif (.NOT.LSAME(TRANS,'N') .AND. .NOT.LSAME(TRANS,'T') .AND.  .NOT.LSAME(TRANS,'C')) THEN
          INFO = 2
      elseif (.NOT.LSAME(DIAG,'U') .AND. .NOT.LSAME(DIAG,'N')) THEN
          INFO = 3
      elseif (N.LT.0) THEN
          INFO = 4
      elseif (INCX.EQ.0) THEN
          INFO = 7
      endif
      IF (INFO.NE.0) THEN
          CALL XERBLA('ZTPMV ',INFO)
          RETURN
      endif
!
!     Quick return if possible.
!
      IF (N.EQ.0) RETURN
!
      NOCONJ = LSAME(TRANS,'T')
      NOUNIT = LSAME(DIAG,'N')
!
!     Set up the start point in X if the increment is not unity. This
!     will be  ( N - 1 )*INCX  too small for descending loops.
!
      IF (INCX.LE.0) THEN
          KX = 1 - (N-1)*INCX
      elseif (INCX.NE.1) THEN
          KX = 1
      endif
!
!     Start the operations. In this version the elements of AP are
!     accessed sequentially with one pass through AP.
!
      IF (LSAME(TRANS,'N')) THEN
!
!        Form  x:= A*x.
!
          IF (LSAME(UPLO,'U')) THEN
              KK = 1
              IF (INCX.EQ.1) THEN
                  DO J = 1,N
                      IF (X(J).NE.ZERO) THEN
                          TEMP = X(J)
                          K = KK
                          DO I = 1,J - 1
                              X(I) = X(I) + TEMP*AP(K)
                              K = K + 1
                          enddo
                          IF (NOUNIT) X(J) = X(J)*AP(KK+J-1)
                      endif
                      KK = KK + J
                  enddo
              ELSE
                  JX = KX
                  DO J = 1,N
                      IF (X(JX).NE.ZERO) THEN
                          TEMP = X(JX)
                          IX = KX
                          DO K = KK,KK + J - 2
                              X(IX) = X(IX) + TEMP*AP(K)
                              IX = IX + INCX
                          enddo
                          IF (NOUNIT) X(JX) = X(JX)*AP(KK+J-1)
                      endif
                      JX = JX + INCX
                      KK = KK + J
                  enddo
              endif
          ELSE
              KK = (N* (N+1))/2
              IF (INCX.EQ.1) THEN
                  DO J = N,1,-1
                      IF (X(J).NE.ZERO) THEN
                          TEMP = X(J)
                          K = KK
                          DO I = N,J + 1,-1
                              X(I) = X(I) + TEMP*AP(K)
                              K = K - 1
                          enddo
                          IF (NOUNIT) X(J) = X(J)*AP(KK-N+J)
                      endif
                      KK = KK - (N-J+1)
                  enddo
              ELSE
                  KX = KX + (N-1)*INCX
                  JX = KX
                  DO J = N,1,-1
                      IF (X(JX).NE.ZERO) THEN
                          TEMP = X(JX)
                          IX = KX
                          DO K = KK,KK - (N- (J+1)),-1
                              X(IX) = X(IX) + TEMP*AP(K)
                              IX = IX - INCX
                          enddo
                          IF (NOUNIT) X(JX) = X(JX)*AP(KK-N+J)
                      endif
                      JX = JX - INCX
                      KK = KK - (N-J+1)
                  enddo
              endif
          endif
      ELSE
!
!        Form  x := A**T*x  or  x := A**H*x.
!
          IF (LSAME(UPLO,'U')) THEN
              KK = (N* (N+1))/2
              IF (INCX.EQ.1) THEN
                  DO J = N,1,-1
                      TEMP = X(J)
                      K = KK - 1
                      IF (NOCONJ) THEN
                          IF (NOUNIT) TEMP = TEMP*AP(KK)
                          DO I = J - 1,1,-1
                              TEMP = TEMP + AP(K)*X(I)
                              K = K - 1
                          enddo
                      ELSE
                          IF (NOUNIT) TEMP = TEMP*DCONJG(AP(KK))
                          DO I = J - 1,1,-1
                              TEMP = TEMP + DCONJG(AP(K))*X(I)
                              K = K - 1
                          enddo
                      endif
                      X(J) = TEMP
                      KK = KK - J
                  enddo
              ELSE
                  JX = KX + (N-1)*INCX
                  DO J = N,1,-1
                      TEMP = X(JX)
                      IX = JX
                      IF (NOCONJ) THEN
                          IF (NOUNIT) TEMP = TEMP*AP(KK)
                          DO K = KK - 1,KK - J + 1,-1
                              IX = IX - INCX
                              TEMP = TEMP + AP(K)*X(IX)
                          enddo
                      ELSE
                          IF (NOUNIT) TEMP = TEMP*DCONJG(AP(KK))
                          DO K = KK - 1,KK - J + 1,-1
                              IX = IX - INCX
                              TEMP = TEMP + DCONJG(AP(K))*X(IX)
                          enddo
                      endif
                      X(JX) = TEMP
                      JX = JX - INCX
                      KK = KK - J
                  enddo
              endif
          ELSE
              KK = 1
              IF (INCX.EQ.1) THEN
                  DO J = 1,N
                      TEMP = X(J)
                      K = KK + 1
                      IF (NOCONJ) THEN
                          IF (NOUNIT) TEMP = TEMP*AP(KK)
                          DO I = J + 1,N
                              TEMP = TEMP + AP(K)*X(I)
                              K = K + 1
                          enddo
                      ELSE
                          IF (NOUNIT) TEMP = TEMP*DCONJG(AP(KK))
                          DO I = J + 1,N
                              TEMP = TEMP + DCONJG(AP(K))*X(I)
                              K = K + 1
                          enddo
                      endif
                      X(J) = TEMP
                      KK = KK + (N-J+1)
                  enddo
              ELSE
                  JX = KX
                  DO J = 1,N
                      TEMP = X(JX)
                      IX = JX
                      IF (NOCONJ) THEN
                          IF (NOUNIT) TEMP = TEMP*AP(KK)
                          DO K = KK + 1,KK + N - J
                              IX = IX + INCX
                              TEMP = TEMP + AP(K)*X(IX)
                          enddo
                      ELSE
                          IF (NOUNIT) TEMP = TEMP*DCONJG(AP(KK))
                          DO K = KK + 1,KK + N - J
                              IX = IX + INCX
                              TEMP = TEMP + DCONJG(AP(K))*X(IX)
                          enddo
                      endif
                      X(JX) = TEMP
                      JX = JX + INCX
                      KK = KK + (N-J+1)
                  enddo
              endif
          endif
      endif

      END SUBROUTINE ZTPMV
