!!            column by column, so that AP( 1 ) contains a( 1, 1 ),
!!            AP( 2 ) and AP( 3 ) contain a( 2, 1 ) and a( 3, 1 )
!!            respectively, and so on.
!!            Note that when DIAG = 'U' or 'u', the diagonal elements of
!!            A are not referenced, but are assumed to be unity.
!!
!!  X
!!
!!           X is complex(kind=real64) array, dimension at least
!!            ( 1 + ( n - 1 )*abs( INCX ) ).
!!            Before entry, the incremented array X must contain the n
!!            element right-hand side vector b. On exit, X is overwritten
!!            with the solution vector x.
!!
!!   INCX
!!
!!           INCX is INTEGER
!!            On entry, INCX specifies the increment for the elements of
!!            X. INCX must not be zero.
!!
!!##AUTHORS
!!
!! + Univ. of Tennessee
!! + Univ. of California Berkeley
!! + Univ. of Colorado Denver
!! + NAG Ltd.
!!
!!  date:December 2016
!!
!!  FURTHER DETAILS
!!
!!   Level 2 Blas routine.
!!
!!   -- Written on 22-October-1986.
!!      Jack Dongarra, Argonne National Lab.
!!      Jeremy Du Croz, Nag Central Office.
!!      Sven Hammarling, Nag Central Office.
!!      Richard Hanson, Sandia National Labs.
!!
!!##SEE ALSO
!!    Online html documentation available at
!!    http://www.netlib.org/lapack/explore-html/
!  =====================================================================
       SUBROUTINE ZTPSV(UPLO,TRANS,DIAG,N,AP,X,INCX)
      implicit none
!
!  -- Reference BLAS level2 routine (version 3.7.0) --
!  -- Reference BLAS is a software package provided by Univ. of Tennessee,    --
!  -- Univ. of California Berkeley, Univ. of Colorado Denver and NAG Ltd..--
!     December 2016
!
!     .. Scalar Arguments ..
      INTEGER,intent(in)                 :: INCX,N
      CHARACTER,intent(in)               :: DIAG,TRANS,UPLO
!     ..
!     .. Array Arguments ..
      complex(kind=real64),intent(in)    :: AP(*)
      complex(kind=real64),intent(inout) :: X(*)
!     ..
!
!  =====================================================================
!
!     .. Parameters ..
      complex(kind=real64) :: ZERO
      PARAMETER (ZERO= (0.0D+0,0.0D+0))
!     ..
!     .. Local Scalars ..
      complex(kind=real64) :: TEMP
      INTEGER I,INFO,IX,J,JX,K,KK,KX
      LOGICAL NOCONJ,NOUNIT
!     ..
!     .. External Functions ..
!      LOGICAL LSAME
!      EXTERNAL LSAME
!     ..
!     .. External Subroutines ..
!      EXTERNAL XERBLA
!     ..
!     .. Intrinsic Functions ..
      INTRINSIC DCONJG
!     ..
!
!     Test the input parameters.
!
      INFO = 0
      IF (.NOT.LSAME(UPLO,'U') .AND. .NOT.LSAME(UPLO,'L')) THEN
          INFO = 1
      elseif (.NOT.LSAME(TRANS,'N') .AND. .NOT.LSAME(TRANS,'T') .AND.  .NOT.LSAME(TRANS,'C')) THEN
          INFO = 2
      elseif (.NOT.LSAME(DIAG,'U') .AND. .NOT.LSAME(DIAG,'N')) THEN
          INFO = 3
      elseif (N.LT.0) THEN
          INFO = 4
      elseif (INCX.EQ.0) THEN
          INFO = 7
      endif
      IF (INFO.NE.0) THEN
          CALL XERBLA('ZTPSV ',INFO)
          RETURN
      endif
!
!     Quick return if possible.
!
      IF (N.EQ.0) RETURN
!
      NOCONJ = LSAME(TRANS,'T')
      NOUNIT = LSAME(DIAG,'N')
!
!     Set up the start point in X if the increment is not unity. This
!     will be  ( N - 1 )*INCX  too small for descending loops.
!
      IF (INCX.LE.0) THEN
          KX = 1 - (N-1)*INCX
      elseif (INCX.NE.1) THEN
          KX = 1
      endif
!
!     Start the operations. In this version the elements of AP are
!     accessed sequentially with one pass through AP.
!
      IF (LSAME(TRANS,'N')) THEN
!
!        Form  x := inv( A )*x.
!
          IF (LSAME(UPLO,'U')) THEN
              KK = (N* (N+1))/2
              IF (INCX.EQ.1) THEN
                  DO J = N,1,-1
                      IF (X(J).NE.ZERO) THEN
                          IF (NOUNIT) X(J) = X(J)/AP(KK)
                          TEMP = X(J)
                          K = KK - 1
                          DO I = J - 1,1,-1
                              X(I) = X(I) - TEMP*AP(K)
                              K = K - 1
                          enddo
                      endif
                      KK = KK - J
                  enddo
              ELSE
                  JX = KX + (N-1)*INCX
                  DO J = N,1,-1
                      IF (X(JX).NE.ZERO) THEN
                          IF (NOUNIT) X(JX) = X(JX)/AP(KK)
                          TEMP = X(JX)
                          IX = JX
                          DO K = KK - 1,KK - J + 1,-1
                              IX = IX - INCX
                              X(IX) = X(IX) - TEMP*AP(K)
                          enddo
                      endif
                      JX = JX - INCX
                      KK = KK - J
                  enddo
              endif
          ELSE
              KK = 1
              IF (INCX.EQ.1) THEN
                  DO J = 1,N
                      IF (X(J).NE.ZERO) THEN
                          IF (NOUNIT) X(J) = X(J)/AP(KK)
                          TEMP = X(J)
                          K = KK + 1
                          DO I = J + 1,N
                              X(I) = X(I) - TEMP*AP(K)
                              K = K + 1
                          enddo
                      endif
                      KK = KK + (N-J+1)
                  enddo
              ELSE
                  JX = KX
                  DO J = 1,N
                      IF (X(JX).NE.ZERO) THEN
                          IF (NOUNIT) X(JX) = X(JX)/AP(KK)
                          TEMP = X(JX)
                          IX = JX
                          DO K = KK + 1,KK + N - J
                              IX = IX + INCX
                              X(IX) = X(IX) - TEMP*AP(K)
                          enddo
                      endif
                      JX = JX + INCX
                      KK = KK + (N-J+1)
                  enddo
              endif
          endif
      ELSE
!
!        Form  x := inv( A**T )*x  or  x := inv( A**H )*x.
!
          IF (LSAME(UPLO,'U')) THEN
              KK = 1
              IF (INCX.EQ.1) THEN
                  DO J = 1,N
                      TEMP = X(J)
                      K = KK
                      IF (NOCONJ) THEN
                          DO I = 1,J - 1
                              TEMP = TEMP - AP(K)*X(I)
                              K = K + 1
                          enddo
                          IF (NOUNIT) TEMP = TEMP/AP(KK+J-1)
                      ELSE
                          DO I = 1,J - 1
                              TEMP = TEMP - DCONJG(AP(K))*X(I)
                              K = K + 1
                          enddo
                          IF (NOUNIT) TEMP = TEMP/DCONJG(AP(KK+J-1))
                      endif
                      X(J) = TEMP
                      KK = KK + J
                  enddo
              ELSE
                  JX = KX
                  DO J = 1,N
                      TEMP = X(JX)
                      IX = KX
                      IF (NOCONJ) THEN
                          DO K = KK,KK + J - 2
                              TEMP = TEMP - AP(K)*X(IX)
                              IX = IX + INCX
                          enddo
                          IF (NOUNIT) TEMP = TEMP/AP(KK+J-1)
                      ELSE
                          DO K = KK,KK + J - 2
                              TEMP = TEMP - DCONJG(AP(K))*X(IX)
                              IX = IX + INCX
                          enddo
                          IF (NOUNIT) TEMP = TEMP/DCONJG(AP(KK+J-1))
                      endif
                      X(JX) = TEMP
                      JX = JX + INCX
                      KK = KK + J
                  enddo
              endif
          ELSE
              KK = (N* (N+1))/2
              IF (INCX.EQ.1) THEN
                  DO J = N,1,-1
                      TEMP = X(J)
                      K = KK
                      IF (NOCONJ) THEN
                          DO I = N,J + 1,-1
                              TEMP = TEMP - AP(K)*X(I)
                              K = K - 1
                          enddo
                          IF (NOUNIT) TEMP = TEMP/AP(KK-N+J)
                      ELSE
                          DO I = N,J + 1,-1
                              TEMP = TEMP - DCONJG(AP(K))*X(I)
                              K = K - 1
                          enddo
                          IF (NOUNIT) TEMP = TEMP/DCONJG(AP(KK-N+J))
                      endif
                      X(J) = TEMP
                      KK = KK - (N-J+1)
                  enddo
              ELSE
                  KX = KX + (N-1)*INCX
                  JX = KX
                  DO J = N,1,-1
                      TEMP = X(JX)
                      IX = KX
                      IF (NOCONJ) THEN
                          DO K = KK,KK - (N- (J+1)),-1
                              TEMP = TEMP - AP(K)*X(IX)
                              IX = IX - INCX
                          enddo
                          IF (NOUNIT) TEMP = TEMP/AP(KK-N+J)
                      ELSE
                          DO K = KK,KK - (N- (J+1)),-1
                              TEMP = TEMP - DCONJG(AP(K))*X(IX)
                              IX = IX - INCX
                          enddo
                          IF (NOUNIT) TEMP = TEMP/DCONJG(AP(KK-N+J))
                      endif
                      X(JX) = TEMP
                      JX = JX - INCX
                      KK = KK - (N-J+1)
                  enddo
              endif
          endif
      endif

      END SUBROUTINE ZTPSV
