!     OF 1 STANDARD DEVIATION.
!     THE RESULTING BOUNDS WILL BE AT MOST
!     1 STANDARD DEVIATION APART.
!
                     ix0 = 0
                     ix1 = N
                     isd = sd + 1.0_wp
                     x2 = ix2
                     CALL BINCDF(x2,Ppar,N,p2)
!
                     IF ( p2<P ) THEN
!
                        ix0 = ix2
                        DO i = 1 , 100000
                           ix2 = ix0 + isd
                           IF ( ix2>=ix1 ) GOTO 200
                           x2 = ix2
                           CALL BINCDF(x2,Ppar,N,p2)
                           IF ( p2>=P ) GOTO 50
                           ix0 = ix2
                        ENDDO
                        WRITE (G_IO,99020)
                        WRITE (G_IO,99005)
!
                        99005 FORMAT (' NO UPPER BOUND FOUND AFTER 10**7 ITERATIONS')
                     ELSE
!
                        ix1 = ix2
                        DO i = 1 , 100000
                           ix2 = ix1 - isd
                           IF ( ix2<=ix0 ) GOTO 200
                           x2 = ix2
                           CALL BINCDF(x2,Ppar,N,p2)
