                        ENDDO
                        WRITE (G_IO,99020)
                        WRITE (G_IO,99006)
                        99006 FORMAT (' NO LOWER BOUND FOUND AFTER 10**7 ITERATIONS')
                     ENDIF
                     GOTO 300
                  ENDIF
               ENDIF
            ENDIF
            Ppf = 0.0_wp
            RETURN
         ENDIF
 20      Ppf = N
         RETURN
      ENDIF
 50   ix1 = ix2
      GOTO 200
   ENDIF
 100  ix0 = ix2
!
 200  IF ( ix0==ix1 ) THEN
      IF ( ix0==0 ) THEN
         ix1 = ix1 + 1
      ELSEIF ( ix0==N ) THEN
         ix0 = ix0 - 1
      ELSE
         WRITE (G_IO,99020)
         WRITE (G_IO,99007)
         99007 FORMAT (' ','LOWER AND UPPER BOUND IDENTICAL')
         GOTO 300
      ENDIF
   ENDIF
