!     COMPUTE BINOMIAL PROBABILITIES FOR THE
!     DERIVED LOWER AND UPPER BOUNDS.
!
   x0 = ix0
   x1 = ix1
   CALL BINCDF(x0,Ppar,N,p0)
   CALL BINCDF(x1,Ppar,N,p1)
!
!     CHECK THE PROBABILITIES FOR PROPER ORDERING
!
   IF ( p0<P .AND. P<=p1 ) THEN
