 50      DO i = 1 , N
            IF ( X(i)<cutoff ) GOTO 100
         ENDDO
         WRITE (G_IO,99011)
         WRITE (G_IO,99012)
         WRITE (G_IO,99013) alph11 , alph12 , sbnam1 , sbnam2
         WRITE (G_IO,99005)
99005    FORMAT (' ','HAS ALL ELEMENTS IN EXCESS OF THE CUTOFF')
         WRITE (G_IO,99006) cutoff
99006    FORMAT (' ','VALUE OF ',E15.8)
         WRITE (G_IO,99011)
         RETURN
      ENDIF
