         RETURN
      ELSEIF ( p0>p1 ) THEN
         WRITE (G_IO,99018)
         WRITE (G_IO,99008)
         99008 FORMAT (' ','LOWER BOUND PROBABILITY (P0) GREATER THAN UPPER BOUND PROBABILITY (P1)')
      ELSEIF ( p0>P ) THEN
