!!       if(allocated(y))deallocate(y)
!!       allocate(y(size(x)))
!!       nu=12
!!       do i=1,size(x)
!!          call tcdf(X(i),Nu,y(i))
!!       enddo
!!       call plott(x,y,size(x))
