'      use,intrinsic :: iso_fortran_env, only: real32,real64,real128', &
'      real,intent(in)                      :: x', &
'      integer,intent(out),optional         :: ierr', &
'      character(len=*),intent(out),optional :: message', &
'      character(len=80)                    :: message_local', &
'      integer                              :: ceiling_robust', &
'      integer                              :: ierr_local', &
