'            close(unit=lun,status=''delete'')', &
'         end program demo_endfile', &
'', &
'SEE ALSO', &
'  BACKSPACE(7), CLOSE(7), ENDFILE(7), FLUSH(7), INQUIRE(7), OPEN(7), PRINT(7),', &
'  READ(7), REWIND(7), WAIT(7), WRITE(7)', &
'', &
'                                June 27, 2025               endfile(7fortran)', &
'']

