'      integer :: x_local', &
'        !', &
'        ! basic', &
'        if(present(x))then', &
'          ! if present, you can use x like any other variable.', &
'          x_local=x', &
'        else', &
'          ! if not, you cannot define or reference x except to', &
'          ! pass it as an optional parameter to another procedure', &
'          ! or in a call to present(3)', &
'          x_local=0', &
'        endif', &
'        !', &
'        func=x_local**2', &
'        !', &
