case('225','stop')

textblock=[character(len=256) :: &
'', &
'stop(7fortran)                                                 stop(7fortran)', &
'', &
'NAME', &
'  STOP(7) - [STATEMENT] initiates termination of execution', &
'', &
'SYNOPSIS', &
'  stop [ stop-code ]', &
'', &
'  error stop [ stop-code ]', &
'', &
'CHARACTERISTICS', &
'  o  STOP-CODE is a constant scalar _character_or integer expression, of', &
