'            write(*,a_commas)''FIRST='',first', &
'            write(*,a_commas)''LAST='',last', &
'            write(*,a_commas)''HAS LENGTH='',last-first.gt.0', &
'         endblock', &
'', &
'         end program demo_tokenize', &
'', &
'  Results:', &
'', &
'       > [  first     ],[second      ],[third      ]', &
'       > [],[first],[],[],[second],[],[third],[],[],[],[],[]', &
